import React, { useState, useContext, useEffect } from "react";
import { AuthContext } from "../services/account.service";

export default function PanelUser() {
  const { getUserInfo } = useContext(AuthContext);
  const [userInfo, setUserInfo] = useState(null);
  useEffect(() => {
    const fetchUserInfo = async () => {
      const data = await getUserInfo();
      setUserInfo(data);
    };
    fetchUserInfo();
  }, [getUserInfo]);

  return (
    <div className=" py-12 rounded-xl p-12 bg-[#5b515197]">
      <div className="px-12">
        <div className="md:w-1/3 w-full bg-white flex justify-center items-center rounded-xl  space-x-1 sm:space-x-4 lg:space-x-8 py-8 px-5 h-44">
          <img src="/images/suivi.png" alt="" className="w-16 h-16 mt-2" />
          <div>
            <p className="mb-2 font-semibold font-mono text-sm">
              Nombres de total de livraisons en cours
            </p>
            <strong className="pl-2 text-xl font-mono">
                {
                    userInfo === null ? "chargement" : userInfo.orders.lenght ? "0" : userInfo.orders.lenght
                }
            </strong>
          </div>
        </div>
      </div>
    </div>
  );
}
