import React, { useState } from "react";
import axios from "axios";

export default function SearchBar({ setResults }) {
  const [value, setValue] = useState("");
  const onChange = (event) => {
    setValue(event.target.value);
  };
  const onSearch = (searchTerm) => {
    setValue(searchTerm);
    console.log("search", searchTerm);
  };

  return (
    <>
      <div className=" border-b-2 border-gray-200 pb-2 flex justify-center items-center md:mt-0 mt-10  w-full ">
        <input
          placeholder="numÃ©ro de suivi"
          type="text"
          aria-label="Search"
          value={value}
          onChange={()=>{onchange()}}
          className="lg:w-96 md:w-72 w-full focus:outline-none placeholder-gray-600 text-base font-normal text-gray-600 leading-4 "
        />
        <svg
          className=" cursor-pointer"
          width="16"
          height="16"
          viewBox="0 0 16 16"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
          onClick={()=>{onSearch(value)}}
        >
          <path
            d="M6.66667 11.3333C9.244 11.3333 11.3333 9.244 11.3333 6.66667C11.3333 4.08934 9.244 2 6.66667 2C4.08934 2 2 4.08934 2 6.66667C2 9.244 4.08934 11.3333 6.66667 11.3333Z"
            stroke="#4B5563"
            strokeWidth="1.25"
            strokeLinecap="round"
            strokeLinejoin="round"
          />
          <path
            d="M14 14L10 10"
            stroke="#4B5563"
            strokeWidth="1.25"
            strokeLinecap="round"
            strokeLinejoin="round"
          />
        </svg>
      </div>
    </>
  );
}
