import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

function RecursiveComponent() {
  const { id } = useParams();
  const [order, setOrder] = useState(null);

  useEffect(() => {
    fetchData(id);
  }, [id]);

  const fetchData = async (id) => {
    try {
      const response = await fetch(`http://localhost:4000/order/${id}`);
      const data = await response.json();
      setOrder(data);
    } catch (error) {
      console.log(error);
    }
  };

  if (!order) {
    return <div>Loading...</div>;
  }

  return (
    <ul>
      <li>Recipient: {order.recipient.name}</li>
      <li>Delivery person: {order.delivery_person.name}</li>
      <li>Destination: {order.destination}</li>
      <li>Payment: {order.payment}</li>
      <li>Total cost: {order.totalCost}</li>
      <li>Status: {order.status ? "Complete" : "Incomplete"}</li>
      <li>Invoice status: {order.invoiceStatus ? "Paid" : "Unpaid"}</li>
      <li>Tracking: {order.tracking}</li>
      <li>Date: {order.date}</li>
    </ul>
  );
}

export default RecursiveComponent;
