import axios from "axios";
import { useState, useEffect } from "react";
import {
  Link,
  useParams,
} from "react-router-dom";

const Order = ({ order }) => {
  const { _id, recipient, delivery_person, destination } = order;
  return (
    <div>
      <h2>Order {_id}</h2>
      <p>Recipient: {recipient.name}</p>
      <p>Delivery Person: {delivery_person.name}</p>
      <p>Destination: {destination}</p>
      <Link to={`/orders/${_id}`}>See Details</Link>
    </div>
  );
};

const OrderDetails = () => {
  const { id } = useParams();
  const [order, setOrder] = useState({});

  useEffect(() => {
    axios.get(`/api/orders/${id}`).then((res) => setOrder(res.data));
  }, [id]);

  const { recipient, delivery_person, destination, totalCost } = order;
  return (
    <div>
      <h3>Order Details</h3>
      <p>Recipient: {recipient.name}</p>
      <p>Delivery Person: {delivery_person.name}</p>
      <p>Destination: {destination}</p>
      <p>Total Cost: {totalCost}</p>
    </div>
  );
};


const Orders = () => {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:4000/orders")
      .then((data) => data.json())
      .then(data => setOrders(data))
      .catch(error => console.log(error))
  }, []);

  return (
    <div>
      {orders.map((order) => (
        <Link key={order._id} to={`/orders/${order._id}`}>
          <Order order={order} />
        </Link>
      ))}
    </div>
  );
};

export default Orders
