import axios from "axios";
import { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import Footer from "../layouts/Footer";
import Navbar from "../layouts/Navbar";

const OrderDetails = () => {
  const { id } = useParams();
  const [order, setOrder] = useState({});

  useEffect(() => {
    axios
      .get(`http://localhost:4000/order/${id}`)
      .then((res) => setOrder(res.data));
  }, [id]);

  const { recipient, delivery_person, destination, totalCost } = order;
  <div>
  <h3>Order Details</h3>
  <p>Recipient: {recipient && recipient.name}</p>
  <p>Delivery Person: {delivery_person && delivery_person.name}</p>
  <p>Destination: {destination}</p>
  <p>Total Cost: {totalCost}</p>
</div>
  return (
    <>
     <div className="flex flex-col h-full  xl:flex-row">
        <div className="xl:w-[17%] w-full">
          <Navbar />
        </div>
        <div className="bg-gray-200 xl:w-[83%] w-full py-4 xl:px-16 px-8 flex flex-col  ">
          <strong className="px-2 py-6 font-mono text-2xl text-gray-800">
            Toutes les commandes
          </strong>
       
          <Footer />
        </div>
      </div>
    </>
  );
};

export default OrderDetails;
