import Cookies from "js-cookie";
import { createContext, useContext, useState, useEffect } from "react";
import { Navigate, useNavigate } from "react-router-dom";

const AuthContext = createContext();

const AuthProvider = ({ children }) => {
  const [userId, setUserId] = useState(
    () => localStorage.getItem("userId") || null
  );
  const [token, setToken] = useState(
    () => localStorage.getItem("token") || null
  );
  const navigate = useNavigate();

  const login = ({ userId, token }) => {
    if (userId !== localStorage.getItem("userId")) logout();
    setUserId(userId);
    setToken(token);
    localStorage.setItem("userId", userId);
    localStorage.setItem("token", token);
    Cookies.get("jwt");
    console.log(userId, token);
  };

  const logout = () => {
    if (userId === localStorage.getItem("userId")) {
      setUserId(null);
      setToken(null);
      localStorage.removeItem("userId");
      localStorage.removeItem("token");
      Cookies.remove("jwt");
      navigate("/");
    } else {
      navigate("/");
    }
  };

  const isAuthenticated = () => {
    return token !== null;
  };

  const getUserInfo = async () => {
    if (!userId) return null; // Ajoutez cette vérification pour éviter d'appeler l'API si userId est null
    const response = await fetch(`http://localhost:4000/user/${userId}`);
    const data = await response.json();
    return data;
  };

  const getOrderByUser = async () => {
    if (!userId) return null; // Ajoutez cette vérification pour éviter d'appeler l'API si userId est null
    const response = await fetch(`http://localhost:4000/user/${userId}/order`);
    const data = await response.json();
    return data;
  };

  return (
    <AuthContext.Provider
      value={{
        userId,
        token,
        login,
        logout,
        isAuthenticated,
        getUserInfo,
        getOrderByUser,
      }}
    >
      {children}
    </AuthContext.Provider>
  );
};

export { AuthProvider, AuthContext };
