import React, { useEffect, useState, useContext } from "react";
import { CardFooter, Button, IconButton } from "@material-tailwind/react";
import { AuthContext } from "../services/account.service";
import "../App.css";
import { useParams } from "react-router-dom";
import axios from "axios";
import Navbar from "../layouts/Navbar";
import Footer from "../layouts/Footer";


export default function UserOrder() {
    const { id } = useParams();
    const [order, setOrder] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
  
    useEffect(() => {
      axios
        .get(`http://localhost:4000/user/${id}/order`)
        .then((res) => setOrder(res.data.orders));
    }, [id]);
   
    const recordsPerPage = 7;
    const lastIndex = currentPage * recordsPerPage;
    const firstIndex = lastIndex - recordsPerPage;
    const currentData = order.slice(firstIndex, lastIndex);
    const npage = Math.ceil(order.length / recordsPerPage);
    const numbers = [...Array(npage + 1).keys()].slice(1);
  
    const prePage = () => {
      if (currentPage !== firstIndex) {
        setCurrentPage(currentPage - 1);
      }
    };
  
    const changePage = (id) => {
      setCurrentPage(id);
    };
  
    const nextPage = () => {
      if (currentPage !== lastIndex) {
        setCurrentPage(currentPage + 1);
      }
    };
  return (
    <>
      <div className="flex flex-col h-full  xl:flex-row">
        <div className="xl:w-[17%] w-full">
          <Navbar />
        </div>
        <div className="bg-gray-200 xl:w-[83%] w-full py-4 xl:px-16 px-8 flex flex-col  ">
        
          <Footer />
        </div>
      </div>
    </>
  );
}
