import axios from "axios";
import { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";

const OrderDetails = () => {
  const { id } = useParams();
  const [order, setOrder] = useState({});

  useEffect(() => {
   function fectOrder(){
      axios.get(`http://localhost:4000/order/${id}`).then((res) => setOrder(res.data));
    }
  }, [id]);

  fectOrder()
  return (
    <div>
     {
      order.map((item,index)=>{
        <ul>
         <li>{item.recipient.name}</li>
         <li>{item.delivery_person.name}</li>
         <li>{item.destination}</li>
         <li>{item.tracking}</li>
        </ul>
      })
     }
    </div>
  );
};

export default OrderDetails;
