import React, { useState, useEffect } from "react";
import axios from "axios";

const Searchbar = () => {
  const [searchItems, setSearchItems] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  const handleChange = (event) => {
    const { value } = event.target;
    setSearchItems(value);
  };

  useEffect(() => {
    if (searchItems.lenght > 0) {
      axios
        .get(`https://localhost:4000/order`)
        .then((res) => setSearchResults(res))
        .then(res => setSearchItems(res))
        .catch((error) => console.log(error));
    }
  }, [searchItems]);

  return (
    <>
      <nav className="border-b-2 border-gray-200 pb-2 flex justify-center items-center md:mt-0 mt-10 w-full">
        <div className="flex justify-center items-center">
          <input
            placeholder="numÃ©ro de suivi"
            type="text"
            aria-label="Search"
            className="lg:w-96 md:w-72 w-full focus:outline-none placeholder-gray-600 text-base font-normal text-gray-600 leading-4"
            value={searchItems}
            onChange={handleChange}
          />
          <svg
            className="cursor-pointer"
            width="16"
            height="16"
            viewBox="0 0 16 16"
            fill="none"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              d="M6.66667 11.3333C9.244 11.3333 11.3333 9.244 11.3333 6.66667C11.3333 4.08934 9.244 2 6.66667 2C4.08934 2 2 4.08934 2 6.66667C2 9.244 4.08934 11.3333 6.66667 11.3333Z"
              stroke="#4B5563"
              strokeWidth="1.25"
              strokeLinecap="round"
              strokeLinejoin="round"
            />
            <path
              d="M14 14L10 10"
              stroke="#4B5563"
              strokeWidth="1.25"
              strokeLinecap="round"
              strokeLinejoin="round"
            />
          </svg>
        </div>
        {searchResults.length > 0 && (
          <ul className="absolute bg-white py-2 mt-2 rounded-md shadow-lg w-full">
            {searchResults.map((result) => (
              <li
                key={result.id}
                className="px-4 py-2 hover:bg-gray-100 cursor-pointer"
              >
                {result.title}
              </li>
            ))}
          </ul>
        )}
      </nav>
    </>
  );
};

export default Searchbar;
